    public long avargeRating(List<Long> longRatings){
        long ratingsSum = 0;
        for (long l : longRatings) {
            ratingsSum = ratingsSum + l;
        }
        double average = ratingsSum/longRatings.size();
        return Double.doubleToLongBits(average);
    }
